///
/// # Example
/// ```no_run
/// use digitalocean::{ ApiClient, apis::droplets };
/// let api = ApiClient::builder("https://api.example.com").build().expect("client");
/// let response = list_neighbors_get(&api)
///     .send()
///     .await?;
/// ```
